import React from 'react';

const VideoDetail = ({ videos, selectedVideo }) => {
   // Outputs for if there is no search.
   if(!selectedVideo) {
      return <h4>Search for a video to watch.</h4>;
   }

   const videoSource = `https://www.youtube.com/embed/${selectedVideo.id.videoId}`;

   return (
      <div>
         <div className="ui embed">
            <iframe 
               src={videoSource} 
               title='Video Player' 
               allowFullScreen={true} 
            />
         </div>
         <h3>{selectedVideo.snippet.title}</h3>
         <p><strong>{selectedVideo.snippet.channelTitle}</strong></p>
         <div className="ui segment">
            <h3 className="ui header">{selectedVideo.snippet.title}</h3>
            <p><strong>{selectedVideo.snippet.channelTitle}</strong></p>
            <p>{selectedVideo.snippet.description}</p>
         </div>
      </div>
   );
}

export default VideoDetail;
